# Description: A novel Firefly Algorithm for black box optimization, where fireflies (solutions) are attracted to brighter fireflies (better solutions) following the inverse-square law of attraction, with randomization for global exploration.
# Code: 
# ```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population = np.random.uniform(-5.0, 5.0, (budget, dim))

    def attractiveness(self, x, y):
        r = np.linalg.norm(x - y)
        return 1 / (1 + r**2)

    def move_fireflies(self, func):
        for i in range(self.budget):
            for j in range(self.budget):
                if func(self.population[j]) < func(self.population[i]):
                    beta = 1.0  # Light absorption coefficient
                    gamma = 0.2  # Randomization parameter
                    step = beta * np.exp(-gamma * np.linalg.norm(self.population[j] - self.population[i])**2) * (self.population[j] - self.population[i])
                    self.population[i] += step
                    self.population[i] = np.clip(self.population[i], -5.0, 5.0)

    def __call__(self, func):
        for _ in range(self.budget):
            self.move_fireflies(func)
        return min(self.population, key=func)

# ```
