# Description: Firework Algorithm (FWA) - a metaheuristic inspired by fireworks explosions where each firework represents a potential solution and exhibits explosion and propagation.
# Code:
# ```python
import numpy as np

class FireworkAlgorithm:
    def __init__(self, budget, dim, num_fireworks=10):
        self.budget = budget
        self.dim = dim
        self.num_fireworks = num_fireworks

    def __call__(self, func):
        def initialize_firework():
            return np.random.uniform(-5.0, 5.0, size=self.dim)

        fireworks = [initialize_firework() for _ in range(self.num_fireworks)]
        best_solution = fireworks[np.argmin([func(firework) for firework in fireworks])]

        for _ in range(self.budget - self.num_fireworks):
            for firework in fireworks:
                new_firework = firework + np.random.normal(0, 1, size=self.dim)
                if func(new_firework) < func(firework):
                    firework[:] = new_firework
            best_solution = fireworks[np.argmin([func(firework) for firework in fireworks])]

        return best_solution
# ```
