import numpy as np

class ImprovedEDSO:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 20
        self.max_iter = budget // self.pop_size
        self.f = 0.5
        self.cr = 0.7
        self.w = 0.9
        self.c1 = 0.1

    def __call__(self, func):
        def init_population():
            return np.random.uniform(-5.0, 5.0, size=(self.pop_size, self.dim))

        def differential_evolution(population):
            new_population = np.copy(population)
            for i in range(self.pop_size):
                a, b, c = np.random.choice(np.delete(np.arange(self.pop_size), i), 3, replace=False)
                j_rand = np.random.randint(self.dim)
                for j in range(self.dim):
                    if np.random.rand() < self.cr or j == j_rand:
                        new_population[i, j] = population[a, j] + self.f * (population[b, j] - population[c, j])
            return new_population

        def particle_swarm_optimization(population):
            best = population[np.argmin([func(ind) for ind in population])]
            for i in range(self.pop_size):
                population[i] = self.w * population[i] + self.c1 * best
            return population

        population = init_population()
        for _ in range(self.max_iter):
            population = differential_evolution(population)
            population = particle_swarm_optimization(population)

        best_solution = population[np.argmin([func(ind) for ind in population])]
        return best_solution
