# Description: A novel metaheuristic algorithm based on Firefly Algorithm with Levy flights for global exploration and firefly attraction for local exploitation to optimize black box functions efficiently.
# Code: 
# ```python
import numpy as np

class FireflyLevyAlgorithm:
    def __init__(self, budget, dim, alpha=1.0, beta=1.0):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta = beta
        self.population = np.random.uniform(-5.0, 5.0, (budget, dim))

    def levy_flight(self):
        sigma = (np.math.gamma(1 + self.beta) * np.math.sin(np.pi * self.beta / 2)) / (np.math.gamma((1 + self.beta) / 2) * self.beta * 2 ** ((self.beta - 1) / 2))
        u = np.random.normal(0, sigma, self.dim)
        v = np.random.normal(0, 1, self.dim)
        step = u / np.abs(v) ** (1 / self.beta)
        return 0.01 * step

    def __call__(self, func):
        for _ in range(self.budget):
            for i in range(self.budget):
                for j in range(self.budget):
                    if func(self.population[i]) < func(self.population[j]):
                        self.population[i] += self.alpha * np.exp(-np.linalg.norm(self.population[j] - self.population[i]) ** 2) * (self.population[j] - self.population[i]) + self.levy_flight()
        best_index = np.argmin([func(ind) for ind in self.population])
        return self.population[best_index]

# ```
