# Description: A Novel Harmony Search Algorithm for Black Box Optimization
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim, hmcr=0.7, par=0.3, bw=0.01):
        self.budget = budget
        self.dim = dim
        self.hmcr = hmcr  # Harmony Memory Consideration Rate
        self.par = par    # Pitch Adjustment Rate
        self.bw = bw      # Bandwidth

    def __call__(self, func):
        def initialize_harmony_memory():
            return np.random.uniform(-5.0, 5.0, (self.budget, self.dim))

        def update_harmony_memory(harmony_memory, new_solution):
            fitness_values = np.array([func(sol) for sol in harmony_memory])
            worst_idx = np.argmax(fitness_values)
            harmony_memory[worst_idx] = new_solution

        harmony_memory = initialize_harmony_memory()
        for _ in range(self.budget):
            new_solution = np.zeros(self.dim)
            for d in range(self.dim):
                if np.random.rand() < self.hmcr:
                    new_solution[d] = harmony_memory[np.random.randint(self.budget)][d]
                else:
                    new_solution[d] = np.random.uniform(-5.0, 5.0)
                if np.random.rand() < self.par:
                    new_solution[d] += np.random.uniform(-1.0, 1.0) * self.bw

            update_harmony_memory(harmony_memory, new_solution)

        return harmony_memory[np.argmin([func(sol) for sol in harmony_memory])]
# ```
