# Description: Novel algorithm using a combination of Differential Evolution and Simulated Annealing to efficiently explore and exploit the search space for optimizing black box functions.
# Code:
# ```python
import numpy as np

class DE_SA_Optimizer:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim

    def __call__(self, func):
        pop_size = 10 * self.dim
        CR = 0.9
        F = 0.8
        T0 = 10.0
        alpha = 0.9

        def mutation(p, q, r, s, F):
            return p + F * (q - r) + F * (s - p)

        def clipToBounds(x):
            return np.clip(x, -5.0, 5.0)

        def acceptance_probability(old_val, new_val, temp):
            return np.exp((old_val - new_val) / temp)

        def evaluate_fitness(individual):
            return func(individual)

        current_population = np.random.uniform(-5.0, 5.0, (pop_size, self.dim))
        current_fitness = np.array([evaluate_fitness(ind) for ind in current_population])

        for _ in range(self.budget):
            for i in range(pop_size):
                a, b, c = np.random.choice(pop_size, 3, replace=False)
                trial_vector = clipToBounds(mutation(current_population[a], current_population[b], current_population[c],
                                                     current_population[i], F))

                trial_fitness = evaluate_fitness(trial_vector)
                if trial_fitness < current_fitness[i]:
                    current_population[i] = trial_vector
                    current_fitness[i] = trial_fitness

                delta_fitness = trial_fitness - current_fitness[i]
                if delta_fitness > 0 and np.random.rand() > acceptance_probability(current_fitness[i], trial_fitness, T0):
                    current_population[i] = trial_vector
                    current_fitness[i] = trial_fitness

            F *= alpha
            T0 *= alpha

        best_idx = np.argmin(current_fitness)
        best_solution = current_population[best_idx]

        return best_solution
# ```
