# Description: Utilizing a hybrid algorithm combining Particle Swarm Optimization and Genetic Algorithm to efficiently explore and converge towards optimal solutions within a limited budget.
# Code:
# ```python
import numpy as np

class HybridPSOGA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 30
        self.max_gen = budget // self.population_size

    def __call__(self, func):
        def fitness(x):
            return func(x)

        def mutate(x, mutation_rate=0.1):
            return x + np.random.uniform(-mutation_rate, mutation_rate, size=self.dim)

        def crossover(parent1, parent2):
            beta = np.random.random(self.dim)
            return beta * parent1 + (1 - beta) * parent2

        def pso_ga():
            population = np.random.uniform(-5.0, 5.0, size=(self.population_size, self.dim))
            fitness_values = np.apply_along_axis(fitness, 1, population)

            for _ in range(self.max_gen):
                best_idx = np.argmin(fitness_values)
                best_solution = population[best_idx]
                new_population = [best_solution]  # Elitism

                for _ in range(self.population_size - 1):
                    parent1, parent2 = population[np.random.choice(self.population_size, size=2, replace=False)]
                    child = crossover(parent1, parent2)
                    child = mutate(child)
                    new_population.append(child)

                population = np.array(new_population)
                fitness_values = np.apply_along_axis(fitness, 1, population)

            best_idx = np.argmin(fitness_values)
            best_solution = population[best_idx]
            return best_solution

        return pso_ga()
# ```
