# Description: A novel metaheuristic algorithm based on the concept of Fireflies' Light Attraction to find optimal solutions in a black box optimization problem.
# Code:
# ```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.alpha = 0.1
        self.beta = 1.0
        self.gamma = 0.2
        self.population = np.random.uniform(-5.0, 5.0, (self.population_size, self.dim))

    def attractiveness(self, x, y):
        return np.exp(-self.gamma * np.linalg.norm(x - y))

    def move_firefly(self, firefly, brightest_firefly):
        r = np.linalg.norm(firefly - brightest_firefly)
        beta_effect = self.beta * np.exp(-self.alpha * r**2)
        delta = beta_effect * (np.random.rand(self.dim) - 0.5)
        return firefly + delta

    def __call__(self, func):
        evaluations = 0
        while evaluations < self.budget:
            for i in range(self.population_size):
                for j in range(self.population_size):
                    if func(self.population[j]) < func(self.population[i]):
                        self.population[i] = self.move_firefly(self.population[i], self.population[j])
                        evaluations += 1
                        if evaluations >= self.budget:
                            break
        best_solution = self.population[np.argmin([func(x) for x in self.population])]
        return best_solution
# ```
