# Description: Firefly Algorithm is a metaheuristic inspired by the flashing behavior of fireflies, where each firefly is attracted to brighter ones and moves towards them to optimize the objective function.
# Code:
# ```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim, alpha=0.5, beta=1.0, gamma=0.1):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha  # Light absorption coefficient
        self.beta = beta    # Attraction coefficient
        self.gamma = gamma  # Step size

    def __call__(self, func):
        def attractiveness(x, y):
            return np.exp(-self.alpha * np.linalg.norm(x - y)**2)

        def move_firefly(current, brightest):
            return current + self.beta * attractiveness(brightest, current) * (brightest - current) + self.gamma * np.random.uniform(-1, 1, self.dim)

        best_solution = np.random.uniform(-5.0, 5.0, self.dim)
        best_fitness = func(best_solution)

        for _ in range(self.budget):
            current_solution = np.random.uniform(-5.0, 5.0, self.dim)
            current_fitness = func(current_solution)

            if current_fitness < best_fitness:
                best_solution = current_solution
                best_fitness = current_fitness

            for _ in range(self.budget):
                new_solution = move_firefly(current_solution, best_solution)
                new_fitness = func(new_solution)

                if new_fitness < current_fitness:
                    current_solution = new_solution
                    current_fitness = new_fitness

                if new_fitness < best_fitness:
                    best_solution = new_solution
                    best_fitness = new_fitness

        return best_solution
# ```
