# Description: Harmony Search Algorithm - a metaheuristic optimization algorithm inspired by the improvisation process of musicians, which utilizes a memory of past solutions to find the optimal solution.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim, harmony_memory_size=20, pitch_adjust_rate=0.1, pitch_adjust_bandwidth=0.01):
        self.budget = budget
        self.dim = dim
        self.harmony_memory_size = harmony_memory_size
        self.pitch_adjust_rate = pitch_adjust_rate
        self.pitch_adjust_bandwidth = pitch_adjust_bandwidth

    def __call__(self, func):
        def random_solution():
            return np.random.uniform(-5.0, 5.0, self.dim)

        def pitch_adjust(solution):
            idx = np.random.choice(self.dim)
            if np.random.rand() < self.pitch_adjust_rate:
                solution[idx] += np.random.uniform(-self.pitch_adjust_bandwidth, self.pitch_adjust_bandwidth)
            return solution

        harmony_memory = [random_solution() for _ in range(self.harmony_memory_size)]
        harmony_memory_fitness = [func(sol) for sol in harmony_memory]

        for _ in range(self.budget - self.harmony_memory_size):
            new_solution = random_solution()
            new_solution_fitness = func(new_solution)

            worst_idx = np.argmax(harmony_memory_fitness)
            if new_solution_fitness < harmony_memory_fitness[worst_idx]:
                harmony_memory[worst_idx] = new_solution
                harmony_memory_fitness[worst_idx] = new_solution_fitness

            for i in range(self.dim):
                harmony_memory[np.random.choice(self.harmony_memory_size)][i] = pitch_adjust(harmony_memory[np.random.choice(self.harmony_memory_size)])[i]

        best_idx = np.argmin(harmony_memory_fitness)
        return harmony_memory[best_idx]
# ```
