# Description: Harmony Search Algorithm for Black Box Optimization with the main idea of using a musical improvisation-inspired algorithm to find optimal solutions by considering the harmony between variables.
# Code: 
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim, hmcr=0.7, par=0.4, bw=0.01):
        self.budget = budget
        self.dim = dim
        self.hmcr = hmcr  # Harmony Memory Considering Rate
        self.par = par  # Pitch Adjustment Rate
        self.bw = bw  # Bandwidth

    def __call__(self, func):
        def initialize_harmony_memory():
            return np.random.uniform(-5.0, 5.0, (self.budget, self.dim))

        def adjust_pitch(harmony):
            new_harmony = np.copy(harmony)
            for i in range(self.dim):
                if np.random.rand() < self.hmcr:
                    rand_index = np.random.randint(self.budget)
                    new_harmony[rand_index, i] += self.par * np.random.uniform(-1, 1)
            return new_harmony

        def random_adjustment(harmony):
            return harmony + self.bw * np.random.uniform(-1, 1, (self.budget, self.dim))

        harmony_memory = initialize_harmony_memory()
        harmony_fitness = np.apply_along_axis(func, 1, harmony_memory)

        for _ in range(self.budget - self.dim):
            new_harmony = adjust_pitch(harmony_memory)
            new_harmony = np.clip(new_harmony, -5.0, 5.0)
            new_harmony = random_adjustment(new_harmony)
            new_fitness = np.apply_along_axis(func, 1, new_harmony)
            replace_idx = np.argmin(harmony_fitness)
            if new_fitness < harmony_fitness[replace_idx]:
                harmony_memory[replace_idx] = new_harmony[np.argmin(new_fitness)]
                harmony_fitness[replace_idx] = new_fitness

        best_idx = np.argmin(harmony_fitness)
        return harmony_memory[best_idx]
