# Description: A novel algorithm inspired by the behavior of fireflies in nature, where fireflies are attracted to other fireflies based on their brightness and move towards them to find optimal solutions in the search space.
# Code:
# ```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.alpha = 0.2
        self.beta0 = 1.0
        self.gamma = 0.1
        self.population = np.random.uniform(-5.0, 5.0, (self.population_size, dim))

    def attractiveness(self, x, y):
        return self.beta0 * np.exp(-self.gamma * np.linalg.norm(x - y))

    def move_fireflies(self, func):
        for _ in range(self.budget):
            for i in range(self.population_size):
                for j in range(self.population_size):
                    if func(self.population[j]) < func(self.population[i]):
                        attractiveness_ij = self.attractiveness(self.population[i], self.population[j])
                        self.population[i] += self.alpha * (self.population[j] - self.population[i]) * attractiveness_ij

    def __call__(self, func):
        self.move_fireflies(func)
        best_solution = self.population[np.argmin([func(x) for x in self.population])]
        return best_solution
# ```
