# Description: An Adaptive Firefly Algorithm that dynamically adjusts its parameters based on the exploration and exploitation phases while mimicking the flashing behavior of fireflies to guide the search.
# Code:
# ```python
import numpy as np

class AdaptiveFireflyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 10
        self.alpha = 0.2
        self.beta_min = 0.2
        self.gamma = 1.0

    def __call__(self, func):
        def attractiveness(distance):
            return self.beta_min * np.exp(-self.gamma * distance**2)

        def explore(current_pos, best_pos):
            new_pos = current_pos + self.alpha * (np.random.rand(self.dim) - 0.5)
            new_pos = np.clip(new_pos, -5.0, 5.0)
            return new_pos if func(new_pos) < func(current_pos) else current_pos

        fireflies = np.random.uniform(-5.0, 5.0, (self.pop_size, self.dim))
        best_firefly = fireflies[np.argmin([func(firefly) for firefly in fireflies])]

        for _ in range(self.budget):
            for i in range(self.pop_size):
                for j in range(self.pop_size):
                    if func(fireflies[j]) < func(fireflies[i]):
                        distance = np.linalg.norm(fireflies[i] - fireflies[j])
                        beta = attractiveness(distance)
                        fireflies[i] = explore(fireflies[i], fireflies[j] + beta * (fireflies[i] - fireflies[j]))

            best_firefly = fireflies[np.argmin([func(firefly) for firefly in fireflies])]

        return best_firefly
