# Description: A novel metaheuristic algorithm named "Harmony Search with Dynamic Ensemble of Improvisation" that combines the harmony search algorithm with an ensemble learning strategy to dynamically select and adapt different improvisation operators during the optimization process.
# Code: 
# ```python
import numpy as np

class HarmonySearchDynamicEnsemble:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def improvisation_operator_1(harmony, rate=0.1):
            rand_idx = np.random.choice(self.dim)
            new_val = harmony[rand_idx] + np.random.uniform(-rate, rate)
            new_val = np.clip(new_val, self.lower_bound, self.upper_bound)
            harmony[rand_idx] = new_val
            return harmony

        def improvisation_operator_2(harmony, rate=0.1):
            rand_idx = np.random.choice(self.dim)
            harmony[rand_idx] = np.random.uniform(self.lower_bound, self.upper_bound)
            return harmony

        def ensemble_improvisation(harmony):
            if np.random.rand() < 0.5:
                return improvisation_operator_1(harmony)
            else:
                return improvisation_operator_2(harmony)

        population = np.random.uniform(self.lower_bound, self.upper_bound, (self.budget, self.dim))
        fitness_values = np.array([func(individual) for individual in population])
        for _ in range(self.budget):
            idx = np.argsort(fitness_values)
            best_harmony = population[idx[0]]
            new_harmony = ensemble_improvisation(best_harmony)
            new_fitness = func(new_harmony)
            if new_fitness < fitness_values[idx[-1]]:
                population[idx[-1]] = new_harmony
                fitness_values[idx[-1]] = new_fitness
        best_idx = np.argmin(fitness_values)
        return population[best_idx]
