# Description: Evolutionary Multi-Objective Optimization Algorithm with Adaptive Differential Evolution (EMOADE) integrates differential evolution with adaptive mechanisms to efficiently explore and exploit the search space for multi-objective optimization tasks.
# Code: 
# ```python
import numpy as np

class EMOADE:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 10  # Population size
        self.cr = 0.5  # Crossover rate
        self.f = 0.5  # Scaling factor

    def __call__(self, func):
        def create_population():
            return np.random.uniform(-5.0, 5.0, (self.pop_size, self.dim))

        def mutate(p, a, b, c):
            return np.clip(a + self.f * (b - c), -5.0, 5.0)

        def crossover(p, trial, dim):
            jrand = np.random.randint(dim)
            y = np.array([trial[i] if (np.random.rand() < self.cr or i == jrand) else p[i] for i in range(dim)])
            return y

        population = create_population()
        fitness = [func(individual) for individual in population]

        for _ in range(self.budget // self.pop_size):
            new_population = []
            for _ in range(self.pop_size):
                idxs = np.random.choice(range(self.pop_size), 3, replace=False)
                a, b, c = population[idxs]
                trial = mutate(population[_], a, b, c)
                trial_fitness = func(trial)
                if trial_fitness < fitness[_]:
                    new_population.append(trial)
                else:
                    new_population.append(population[_])
            population = np.array(new_population)
            fitness = [func(individual) for individual in population]

        return population[np.argmin(fitness)]

# ```
