# Description: Firefly Algorithm (FA) is a metaheuristic algorithm inspired by the flashing behavior of fireflies to find optimal solutions by mimicking the attractiveness of fireflies' flashing light.
# Code:
# ```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim, alpha=0.01, beta0=1.0):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta0 = beta0
        self.population = np.random.uniform(-5.0, 5.0, (budget, dim))

    def __call__(self, func):
        for t in range(self.budget):
            for i in range(self.budget):
                for j in range(self.budget):
                    if func(self.population[j]) < func(self.population[i]):
                        self.population[i] += self.beta0 * np.exp(-self.alpha * np.linalg.norm(self.population[j] - self.population[i])) * (self.population[j] - self.population[i])
            self.population = np.clip(self.population, -5.0, 5.0)

        best_solution = min(self.population, key=lambda x: func(x))
        return best_solution
# ```
