# Description: A novel metaheuristic algorithm called Harmony Search Algorithm (HSA) that mimics the improvisation process in music to find optimal solutions in continuous search spaces.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim, hmcr=0.7, par=0.4, bw=0.01):
        self.budget = budget
        self.dim = dim
        self.hmcr = hmcr  # Harmony Memory Consideration Rate
        self.par = par    # Pitch Adjustment Rate
        self.bw = bw      # Bandwidth for pitch adjustment

    def __call__(self, func):
        def initialize_harmony_memory():
            return np.random.uniform(-5.0, 5.0, (self.budget, self.dim))

        def improvise(harmony_memory):
            new_harmony = np.copy(harmony_memory)
            for i in range(self.dim):
                if np.random.rand() < self.hmcr:
                    new_harmony[:, i] = np.random.choice(harmony_memory[:, i])
                    if np.random.rand() < self.par:
                        new_harmony[:, i] += np.random.uniform(-self.bw, self.bw, self.budget)
            return new_harmony

        harmony_memory = initialize_harmony_memory()
        harmony_costs = np.array([func(x) for x in harmony_memory])

        for _ in range(self.budget - self.dim):
            new_harmony = improvise(harmony_memory)
            new_costs = np.array([func(x) for x in new_harmony])
            replace_idx = np.where(new_costs < harmony_costs)[0]
            harmony_memory[replace_idx] = new_harmony[replace_idx]
            harmony_costs[replace_idx] = new_costs[replace_idx]

        best_idx = np.argmin(harmony_costs)
        return harmony_memory[best_idx]
