# Description: Enhanced Harmony Search Algorithm with Pitch Adjustment for Improved Convergence
# Code: 
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.bandwidth = 0.5  # Introducing pitch adjustment bandwidth

    def generate_harmony(self):
        return np.random.uniform(self.lower_bound, self.upper_bound, self.dim)

    def adjust_pitch(self, harmony):
        return harmony + np.random.uniform(-self.bandwidth, self.bandwidth, self.dim)  # Introducing pitch adjustment

    def __call__(self, func):
        harmonies = [self.generate_harmony() for _ in range(self.budget)]
        for _ in range(self.budget):
            new_harmony = self.adjust_pitch(min(harmonies, key=func))
            if func(new_harmony) < func(min(harmonies, key=func)):
                harmonies.remove(min(harmonies, key=func))
                harmonies.append(new_harmony)
        best_harmony = min(harmonies, key=func)
        return best_harmony
