# Description: A novel metaheuristic algorithm inspired by the behavior of swarming birds, where each solution is represented as a bird in a flock that adjusts its position based on the collective movement towards the global best solution.
# Code:
# ```python
import numpy as np

class BirdFlockOptimizer:
    def __init__(self, budget, dim, flock_size=20, inertia_weight=0.5, cognitive_weight=1.5, social_weight=1.5):
        self.budget = budget
        self.dim = dim
        self.flock_size = flock_size
        self.inertia_weight = inertia_weight
        self.cognitive_weight = cognitive_weight
        self.social_weight = social_weight

    def __call__(self, func):
        def initialize_flock():
            return np.random.uniform(-5.0, 5.0, size=(self.flock_size, self.dim))

        def optimize_flock():
            flock = initialize_flock()
            best_position = np.copy(flock[0])
            for _ in range(self.budget):
                fitness_values = [func(bird) for bird in flock]
                best_bird_idx = np.argmin(fitness_values)
                best_bird = flock[best_bird_idx]

                for i in range(self.flock_size):
                    velocity = self.inertia_weight * (flock[i] - flock[i]) + \
                               self.cognitive_weight * np.random.rand() * (best_bird - flock[i]) + \
                               self.social_weight * np.random.rand() * (best_position - flock[i])
                    flock[i] = flock[i] + velocity
                    flock[i] = np.clip(flock[i], -5.0, 5.0)  # Ensure within bounds

                if fitness_values[best_bird_idx] < func(best_position):
                    best_position = np.copy(flock[best_bird_idx])

            return best_position

        return optimize_flock()
# ```
