# Description: Harmony Search Algorithm is inspired by the musical process of searching for a perfect state of harmony within a group, where new solutions are created through the combination of existing solutions.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def generate_random_solution():
            return np.random.uniform(self.lower_bound, self.upper_bound, self.dim)

        def improvise_harmony(memory, harmony_rate=0.7):
            new_harmony = np.zeros(self.dim)
            for i in range(self.dim):
                if np.random.rand() < harmony_rate:
                    new_harmony[i] = memory[np.random.randint(0, len(memory))]
                else:
                    new_harmony[i] = np.random.uniform(self.lower_bound, self.upper_bound)
            return new_harmony

        # Initialization
        harmony_memory = [generate_random_solution() for _ in range(10)]

        for _ in range(self.budget):
            new_harmony = improvise_harmony(harmony_memory)
            if func(new_harmony) < func(harmony_memory[-1]):
                harmony_memory[-1] = new_harmony
                harmony_memory = sorted(harmony_memory, key=lambda x: func(x))

        return harmony_memory[0]
# ```
