# Description: Harmony search algorithm generates new solutions by considering the historical best solutions and randomness, aiming to find the global optimum.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def generate_random_solution():
            return np.random.uniform(self.lower_bound, self.upper_bound, self.dim)

        def improvise_harmony(memory, bandwidth):
            new_harmony = np.zeros(self.dim)
            for d in range(self.dim):
                if np.random.rand() < bandwidth:
                    new_harmony[d] = np.random.uniform(self.lower_bound, self.upper_bound)
                else:
                    idx = np.random.randint(len(memory))
                    new_harmony[d] = memory[idx][d]
            return new_harmony

        memory = [generate_random_solution() for _ in range(10)]
        bandwidth = 0.01

        for _ in range(self.budget):
            new_solution = improvise_harmony(memory, bandwidth)
            if func(new_solution) < func(memory[-1]):
                memory[-1] = new_solution
                memory = sorted(memory, key=lambda x: func(x))

        return memory[0]
# ```
