# Description: Novel Black Hole Optimization Algorithm - Simulating the behavior of a black hole to attract and consume solutions to find optimal solutions in the search space.
# Code:
# ```python
import numpy as np

class BlackHoleOptimization:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def attractor(x, x_attractor, G):
            r = np.random.uniform(0, 1)
            beta = 2
            sigma = (1+1/G) * beta
            return x + sigma * r * (x_attractor - x)

        def constraint_check(x):
            return np.clip(x, self.lower_bound, self.upper_bound)

        G = 1
        population = np.random.uniform(self.lower_bound, self.upper_bound, (self.budget, self.dim))
        fitness = np.array([func(individual) for individual in population])

        for _ in range(1, self.budget):
            sorted_indices = np.argsort(fitness)
            best_individual = population[sorted_indices[0]]

            for i in range(1, self.budget):
                new_individual = attractor(population[sorted_indices[i]], best_individual, G)
                new_individual = constraint_check(new_individual)
                if func(new_individual) < fitness[sorted_indices[i]]:
                    population[sorted_indices[i]] = new_individual
                    fitness[sorted_indices[i]] = func(new_individual)

            G += 1

        return best_individual
# ```

# Description: A novel metaheuristic algorithm combining Differential Evolution and Particle Swarm Optimization for black box optimization.
# Code: 
# ```python
from scipy.optimize import differential_evolution
from pyswarm import pso

class DE_PSO_Optimizer:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim

    def __call__(self, func):
        def combined_func(x):
            return func(x)

        bounds = [(-5.0, 5.0) for _ in range(self.dim)]

        def de_callback(x, f, context, **kwargs):
            context.append(x)

        de_result = differential_evolution(combined_func, bounds, callback=de_callback, disp=False)
        de_population = de_result.populations

        pso_result = pso(combined_func, lb=[-5.0] * self.dim, ub=[5.0] * self.dim, maxiter=100, callback=de_callback, context=de_population)
        return pso_result[0]

# ```
