# Description: Particle Swarm Optimization with dynamic parameter adaptation based on Differential Evolution strategy.
# Code:
# ```python
import numpy as np

class PSO_DE:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 30
        self.w = 0.7
        self.c1 = 1.49445
        self.c2 = 1.49445
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def within_bounds(x):
            return np.clip(x, self.lower_bound, self.upper_bound)

        def de_rand_1_bin(p, a, b, c, f):
            return within_bounds(a + f * (b - c))

        def objective_function_eval(x):
            return func(x)

        population = np.random.uniform(self.lower_bound, self.upper_bound, (self.pop_size, self.dim))
        velocities = np.zeros((self.pop_size, self.dim))
        personal_best_pos = population.copy()
        personal_best_val = np.array([objective_function_eval(x) for x in population])
        global_best_idx = np.argmin(personal_best_val)
        global_best_pos = personal_best_pos[global_best_idx].copy()

        for _ in range(self.budget):
            r1, r2 = np.random.rand(self.pop_size, self.dim), np.random.rand(self.pop_size, self.dim)
            velocities = self.w * velocities + self.c1 * r1 * (personal_best_pos - population) + self.c2 * r2 * (global_best_pos - population)
            population = within_bounds(population + velocities)
            for i in range(self.pop_size):
                trial_vector = de_rand_1_bin(population[i], population[np.random.choice(self.pop_size, 3, replace=False)], population[np.random.choice(self.pop_size)], population[np.random.choice(self.pop_size)], np.random.normal())
                trial_fitness = objective_function_eval(trial_vector)
                if trial_fitness < personal_best_val[i]:
                    personal_best_val[i] = trial_fitness
                    personal_best_pos[i] = trial_vector
                    if trial_fitness < personal_best_val[global_best_idx]:
                        global_best_idx = i
                        global_best_pos = trial_vector
        return global_best_pos
# ```
