# Description: A novel metaheuristic algorithm based on Binary Differential Evolution with opposition-based initialization for black box optimization problems.
# Code:
# ```python
import numpy as np

class BinaryDifferentialEvolution:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 10
        self.F = 0.5
        self.CR = 0.9
        self.opp_prob = 0.1

    def initialize_population(self):
        return np.random.choice([0, 1], size=(self.pop_size, self.dim))

    def mutate(self, population, target_idx):
        a, b, c = np.random.choice(len(population), 3, replace=False)
        mutant = np.copy(population[target_idx])
        for i in range(self.dim):
            if np.random.rand() < self.CR:
                mutant[i] = population[a][i] ^ (population[b][i] | population[c][i])
        return mutant

    def select(self, population, target_idx, mutant):
        fitness_target = func(population[target_idx])
        fitness_trial = func(mutant)
        if fitness_trial < fitness_target:
            population[target_idx] = mutant
        return population

    def __call__(self, func):
        population = self.initialize_population()
        for _ in range(self.budget):
            for i in range(self.pop_size):
                mutant = self.mutate(population, i)
                population = self.select(population, i, mutant)
        best_solution = population[np.argmin([func(individual) for individual in population])]
        return best_solution
