# Description: A novel hybrid metaheuristic algorithm combining Particle Swarm Optimization (PSO) with Cuckoo Search (CS) for global and local exploration, respectively, to efficiently optimize black box functions in a diverse search space.
# Code:
# ```python
import numpy as np

class HybridPSOCS:
    def __init__(self, budget, dim, num_particles=30, max_iter=100):
        self.budget = budget
        self.dim = dim
        self.num_particles = num_particles
        self.max_iter = max_iter

    def __call__(self, func):
        def pso_update_position(position, velocity):
            new_position = position + velocity
            new_position = np.clip(new_position, -5.0, 5.0)  # Constraint within search space
            return new_position

        def pso_evaluate_particle(particle):
            return func(particle)

        def initialize_particles():
            particles = np.random.uniform(low=-5.0, high=5.0, size=(self.num_particles, self.dim))
            velocities = np.zeros((self.num_particles, self.dim))
            return particles, velocities

        particles, velocities = initialize_particles()
        global_best = particles[np.argmin([pso_evaluate_particle(p) for p in particles])]
        for _ in range(self.max_iter):
            for i in range(self.num_particles):
                new_velocity = 0.5 * velocities[i] + 2.0 * np.random.random() * (global_best - particles[i]) + 2.0 * np.random.random() * (global_best - particles[i])
                particles[i] = pso_update_position(particles[i], new_velocity)
                if pso_evaluate_particle(particles[i]) < pso_evaluate_particle(global_best):
                    global_best = particles[i]
            if np.sum([pso_evaluate_particle(p) for p in particles]) >= self.budget:
                break
        return global_best
# ```
