# Description: Novel metaheuristic algorithm utilizing a combination of Particle Swarm Optimization (PSO), Differential Evolution (DE), and Simulated Annealing (SA) for black box optimization problems.
# Code:
# ```python
import numpy as np

class HybridMetaheuristic:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 10
        self.max_iters = budget // self.population_size

    def __call__(self, func):
        def objective_function(x):
            return func(x)

        def mutate(x, r1, r2, r3, alpha=0.5):
            return x + alpha * (r1 - x) + alpha * (r2 - r3)

        def annealing(x, T):
            return x + np.random.normal(0, T)

        def optimize_population(population, fitness):
            best_idx = np.argmin(fitness)
            best_solution = population[best_idx]
            for i in range(len(population)):
                if np.random.rand() < 0.3:
                    population[i] = mutate(population[i], population[np.random.randint(len(population))],
                                           population[np.random.randint(len(population))], best_solution)
                else:
                    population[i] = annealing(population[i], 1 / np.log(1 + len(population))**2)

        # Initialization
        population = np.random.uniform(-5.0, 5.0, size=(self.population_size, self.dim))
        fitness = np.array([objective_function(x) for x in population])

        for _ in range(self.max_iters):
            optimize_population(population, fitness)
            fitness = np.array([objective_function(x) for x in population])

        best_idx = np.argmin(fitness)
        best_solution = population[best_idx]
        return best_solution
# ```
