# Description: Hybrid Firefly Algorithm with Differential Evolution (HFA-DE): A novel metaheuristic algorithm combining the exploration capability of firefly algorithm with the exploitation ability of differential evolution for black box optimization.
# Code: 
# ```python
import numpy as np

class HFA_DE:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.alpha = 0.5
        self.beta_min = 0.2
        self.gamma = 1.0

    def __call__(self, func):
        pop = np.random.uniform(-5.0, 5.0, (self.population_size, self.dim))
        budget_per_iteration = self.budget // self.population_size

        for _ in range(budget_per_iteration):
            for i in range(self.population_size):
                for j in range(self.population_size):
                    if func(pop[j]) < func(pop[i]):
                        step = self.alpha * (1.0 - np.exp(-self.gamma * np.linalg.norm(pop[j] - pop[i])))
                        pop[i] += step * (pop[j] - pop[i]) + self.beta_min * np.random.uniform(-1, 1, self.dim)

            for i in range(self.population_size):
                trial_vector = pop[i] + 0.8 * (pop[np.random.choice(self.population_size)] - pop[np.random.choice(self.population_size)])
                if func(trial_vector) < func(pop[i]):
                    pop[i] = trial_vector

        best_solution = pop[np.argmin([func(x) for x in pop])]
        return best_solution
# ```
