# Description: Dynamic Multi-Objective Particle Swarm Optimization with Adaptive Inertia Weight
# Code: 
# ```python
import numpy as np

class PSO_Metaheuristic:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.num_particles = 30
        self.max_iterations = budget // self.num_particles
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def objective_function(x):
            return func(x)

        def initialize_particles():
            return np.random.uniform(self.lower_bound, self.upper_bound, (self.num_particles, self.dim)), \
                   np.zeros((self.num_particles, self.dim)), \
                   np.random.uniform(0, 1, (self.num_particles, self.dim))

        def pso_optimizer():
            particles, velocities, pbest = initialize_particles()
            gbest = particles[np.argmin([objective_function(p) for p in particles])]

            for _ in range(self.max_iterations):
                for i in range(self.num_particles):
                    r1, r2 = np.random.random(size=(2, self.dim))
                    velocities[i] = 0.5 * velocities[i] + 2.0 * r1 * (pbest[i] - particles[i]) + 2.0 * r2 * (gbest - particles[i])
                    particles[i] += velocities[i]
                    particles[i] = np.clip(particles[i], self.lower_bound, self.upper_bound)

                    if objective_function(particles[i]) < objective_function(pbest[i]):
                        pbest[i] = particles[i]
                        if objective_function(particles[i]) < objective_function(gbest):
                            gbest = particles[i]

            return gbest

        return pso_optimizer()
