# Description: Harmony Search with Dynamic Pitch Adjustment: A metaheuristic algorithm inspired by the improvisation process of musicians, where solutions harmonize by adjusting their pitch dynamically to search for the optimal solution.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim, hmcr=0.7, par=0.3, bandwidth=0.01):
        self.budget = budget
        self.dim = dim
        self.hmcr = hmcr
        self.par = par
        self.bandwidth = bandwidth

    def __call__(self, func):
        harmony_memory = np.random.uniform(-5.0, 5.0, (self.budget, self.dim))
        harmony_fitness = np.array([func(h) for h in harmony_memory])

        for _ in range(self.budget - len(harmony_memory)):
            new_solution = np.zeros(self.dim)
            for i in range(self.dim):
                if np.random.rand() < self.hmcr:
                    if np.random.rand() < self.par:
                        new_solution[i] = np.random.uniform(-5.0, 5.0)
                    else:
                        idx = np.random.randint(0, len(harmony_memory))
                        new_solution[i] = harmony_memory[idx, i]
                else:
                    new_solution[i] = np.random.uniform(-5.0, 5.0)
                    new_solution[i] += np.random.uniform(-self.bandwidth, self.bandwidth)

            new_fitness = func(new_solution)
            if new_fitness < np.max(harmony_fitness):
                idx = np.argmax(harmony_fitness)
                harmony_memory[idx] = new_solution
                harmony_fitness[idx] = new_fitness

        return harmony_memory[np.argmin(harmony_fitness)]
# ```
