# Description: Harmony Search Algorithm - A metaheuristic optimization algorithm inspired by the improvisation process of music players, where new solutions are generated by harmonizing existing solutions based on memory consideration, pitch adjustment, and random exploration.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim, harmony_memory_size=20, pitch_adjust_rate=0.1, bandwidth=0.01):
        self.budget = budget
        self.dim = dim
        self.harmony_memory_size = harmony_memory_size
        self.pitch_adjust_rate = pitch_adjust_rate
        self.bandwidth = bandwidth

    def __call__(self, func):
        def random_solution():
            return np.random.uniform(-5.0, 5.0, self.dim)

        def pitch_adjust(solution):
            index = np.random.randint(self.dim)
            if np.random.rand() < self.pitch_adjust_rate:
                solution[index] += self.bandwidth * np.random.uniform(-1, 1)
            return np.clip(solution, -5.0, 5.0)

        harmony_memory = [random_solution() for _ in range(self.harmony_memory_size)]
        while self.budget > 0:
            new_solution = pitch_adjust(harmony_memory[np.random.randint(self.harmony_memory_size)])
            if func(new_solution) < func(harmony_memory[-1]):
                harmony_memory[-1] = new_solution
            self.budget -= 1
        return harmony_memory[-1]
# ```
