# Description: Firework Algorithm (FWA) is inspired by the behavior of fireworks exploding in the sky, with sparks representing potential solutions exploring the search space and explosions indicating global search.
# Code:
# ```python
import numpy as np

class FireworkAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim

    def __call__(self, func):
        def initialize_firework():
            return np.random.uniform(low=-5.0, high=5.0, size=self.dim)

        def explode(firework, sparks):
            for _ in range(self.budget):
                for _ in range(sparks):
                    new_solution = firework + np.random.normal(0, 1, self.dim)
                    new_solution = np.clip(new_solution, -5.0, 5.0)
                    if func(new_solution) < func(firework):
                        firework = new_solution
            return firework

        firework = initialize_firework()
        best_solution = firework.copy()
        best_fitness = func(firework)

        while self.budget > 0:
            sparks = np.random.poisson(lam=10)
            firework = explode(firework, sparks)
            current_fitness = func(firework)
            if current_fitness < best_fitness:
                best_solution = firework.copy()
                best_fitness = current_fitness
            self.budget -= sparks

        return best_solution
# ```
