# Description: Metaheuristic algorithm inspired by the behavior of social insects, where individuals locally interact to find optimal solutions.
# Code: 
# ```python
import numpy as np

class AntColonyOptimization:
    def __init__(self, budget, dim, colony_size=50, max_iter=100):
        self.budget = budget
        self.dim = dim
        self.colony_size = colony_size
        self.max_iter = max_iter

    def __call__(self, func):
        def evaluate_solution(solution):
            return func(solution)

        def initialize_colony():
            return np.random.uniform(-5.0, 5.0, size=(self.colony_size, self.dim))

        def probabilistic_selection(probabilities):
            return np.random.choice(np.arange(len(probabilities)), p=probabilities)

        colony = initialize_colony()
        best_solution = colony[np.argmin([evaluate_solution(sol) for sol in colony])]

        for _ in range(self.max_iter):
            pheromones = np.zeros((self.colony_size, self.dim))
            new_colony = np.copy(colony)
            for ant_id in range(self.colony_size):
                ant_solution = new_colony[ant_id]
                for dim_id in range(self.dim):
                    probabilities = np.exp(pheromones[:, dim_id]) / np.sum(np.exp(pheromones[:, dim_id]))
                    selected_ant_id = probabilistic_selection(probabilities)
                    new_colony[ant_id, dim_id] = colony[selected_ant_id, dim_id]

                if evaluate_solution(new_colony[ant_id]) < evaluate_solution(ant_solution):
                    pheromones[ant_id] += 1 / evaluate_solution(ant_solution)

            colony = np.copy(new_colony)
            current_best_solution = colony[np.argmin([evaluate_solution(sol) for sol in colony])]
            if evaluate_solution(current_best_solution) < evaluate_solution(best_solution):
                best_solution = np.copy(current_best_solution)

        return best_solution
