# Description: Novel algorithm combining Particle Swarm Optimization and Differential Evolution with adaptive strategies to efficiently explore and exploit the search space in black box optimization problems.
# Code:
# ```python
import numpy as np

class HybridPSODE:
    def __init__(self, budget, dim, nparticles=30, cr=0.5, f=0.5, w=0.5):
        self.budget = budget
        self.dim = dim
        self.nparticles = nparticles
        self.cr = cr
        self.f = f
        self.w = w

    def __call__(self, func):
        def fitness(x):
            return func(x)

        def create_particle():
            return np.random.uniform(-5.0, 5.0, self.dim)

        def clip(x):
            return np.clip(x, -5.0, 5.0)

        best = None
        swarm = [create_particle() for _ in range(self.nparticles)]
        velocities = [np.zeros(self.dim) for _ in range(self.nparticles)]

        for _ in range(self.budget):
            for i in range(self.nparticles):
                r1, r2, r3 = np.random.choice(self.nparticles, 3, replace=False)
                new_velocity = self.w * velocities[i] + self.f * (swarm[r1] - swarm[i]) + self.cr * (swarm[r2] - swarm[r3])
                new_position = clip(swarm[i] + new_velocity)

                if fitness(new_position) < fitness(swarm[i]):
                    swarm[i] = new_position
                    velocities[i] = new_velocity

                if best is None or fitness(swarm[i]) < fitness(best):
                    best = swarm[i]

        return best
# ```
