# Description: A novel metaheuristic algorithm based on Firefly Algorithm with adaptive step size and dynamic attractiveness to optimize black box functions.
# Code:
# ```python
import numpy as np

class FireflyOptimization:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population = np.random.uniform(-5.0, 5.0, (budget, dim))
        self.alpha = 0.5  # Alpha parameter for attractiveness
        self.beta_min = 0.2  # Minimum beta parameter for step size
        self.beta_max = 1.0  # Maximum beta parameter for step size

    def attractiveness(self, x, y):
        return np.exp(-np.sum((x - y) ** 2) / self.dim)

    def __call__(self, func):
        for _ in range(self.budget):
            for i in range(len(self.population)):
                for j in range(len(self.population)):
                    if func(self.population[j]) < func(self.population[i]):
                        beta = self.beta_min + (self.beta_max - self.beta_min) * np.random.rand()
                        self.population[i] += beta * (self.population[j] - self.population[i]) * self.attractiveness(self.population[i], self.population[j])
        return self.population[np.argmin([func(p) for p in self.population])]
# ```
