# Description: The algorithm utilizes a combination of Particle Swarm Optimization (PSO) and Genetic Algorithms (GA) to explore the search space efficiently and exploit promising regions.
# Code:
# ```python
import numpy as np

class HybridPSOGA:
    def __init__(self, budget, dim, swarm_size=20, mutation_rate=0.1):
        self.budget = budget
        self.dim = dim
        self.swarm_size = swarm_size
        self.mutation_rate = mutation_rate

    def __call__(self, func):
        swarm = np.random.uniform(-5.0, 5.0, (self.swarm_size, self.dim))
        pbest = swarm.copy()
        gbest = pbest[np.argmin([func(individual) for individual in pbest])]

        for _ in range(self.budget):
            fitness_values = np.array([func(individual) for individual in swarm])
            best_index = np.argmin(fitness_values)
            if fitness_values[best_index] < func(gbest):
                gbest = swarm[best_index].copy()

            for idx, individual in enumerate(swarm):
                if np.random.rand() < self.mutation_rate:
                    swarm[idx] = np.clip(individual + np.random.normal(0, 1, self.dim), -5.0, 5.0)
                else:
                    swarm[idx] = individual + np.random.normal(0, 1, self.dim)

            pbest_values = np.array([func(individual) for individual in pbest])
            for idx, individual in enumerate(swarm):
                if func(individual) < pbest_values[idx]:
                    pbest[idx] = individual.copy()

        return gbest
# ```
