# Description: Firefly Algorithm is inspired by the flashing behavior of fireflies to optimize solutions by attractiveness and distance.
# Code:
```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget=10000, dim=10, alpha=0.5, beta0=1.0, gamma=0.1):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta0 = beta0
        self.gamma = gamma
        self.f_opt = np.Inf
        self.x_opt = None

    def attractiveness(self, r):
        return self.beta0 * np.exp(-self.gamma * r**2)

    def move_fireflies(self, x, f, func):
        for i in range(self.budget):
            for j in range(self.budget):
                if f[j] < f[i]:
                    r = np.linalg.norm(x[i] - x[j])
                    beta = self.attractiveness(r)
                    x[i] += self.alpha * (x[j] - x[i]) * beta
                    f[i] = func(x[i])
                    if f[i] < self.f_opt:
                        self.f_opt = f[i]
                        self.x_opt = x[i]

    def __call__(self, func):
        x = np.random.uniform(func.bounds.lb, func.bounds.ub, size=(self.budget, self.dim))
        f = np.array([func(x_i) for x_i in x])

        self.move_fireflies(x, f, func)

        return self.f_opt, self.x_opt
```

# Description: Improved Firefly Algorithm with dynamic alpha and gamma values based on iteration progress.
# Code:
```python
class ImprovedFireflyAlgorithm(FireflyAlgorithm):
    def __init__(self, budget=10000, dim=10, alpha_init=0.5, gamma_init=0.1):
        super().__init__(budget, dim)
        self.alpha_init = alpha_init
        self.gamma_init = gamma_init

    def dynamic_params(self, t):
        alpha_t = self.alpha_init * np.exp(-0.1 * t)
        gamma_t = self.gamma_init * np.exp(-0.1 * t)
        return alpha_t, gamma_t

    def __call__(self, func):
        x = np.random.uniform(func.bounds.lb, func.bounds.ub, size=(self.budget, self.dim))
        f = np.array([func(x_i) for x_i in x])

        for t in range(self.budget):
            alpha_t, gamma_t = self.dynamic_params(t)
            for i in range(self.budget):
                for j in range(self.budget):
                    if f[j] < f[i]:
                        r = np.linalg.norm(x[i] - x[j])
                        beta = self.attractiveness(r)
                        x[i] += alpha_t * (x[j] - x[i]) * beta
                        f[i] = func(x[i])
                        if f[i] < self.f_opt:
                            self.f_opt = f[i]
                            self.x_opt = x[i]

        return self.f_opt, self.x_opt
