# Description: A novel optimization algorithm combining Differential Evolution (DE) with Local Search. DE is used for global exploration, and Local Search is applied to exploit promising regions.
# Code:
# ```python
import numpy as np

class NovelMetaheuristic:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 10
        self.cr = 0.5
        self.f = 0.5
        self.bounds = (-5.0, 5.0)

    def de_mutate(self, population, target_idx):
        r1, r2, r3 = np.random.choice(len(population), 3, replace=False)
        mutant = population[r1] + self.f * (population[r2] - population[r3])
        return np.clip(mutant, self.bounds[0], self.bounds[1])

    def de_crossover(self, target, mutant):
        trial = np.copy(target)
        for i in range(self.dim):
            if np.random.rand() > self.cr:
                trial[i] = mutant[i]
        return trial

    def local_search(self, current, func):
        candidate = np.copy(current)
        for _ in range(5):
            noise = np.random.uniform(-0.1, 0.1, self.dim)
            candidate = np.clip(candidate + noise, self.bounds[0], self.bounds[1])
            if func(candidate) < func(current):
                current = np.copy(candidate)
        return current

    def __call__(self, func):
        population = np.random.uniform(self.bounds[0], self.bounds[1], (self.pop_size, self.dim))
        evaluations = 0
        best_solution = None
        while evaluations < self.budget:
            for i in range(self.pop_size):
                target = population[i]
                mutant = self.de_mutate(population, i)
                trial = self.de_crossover(target, mutant)
                trial = self.local_search(trial, func)
                if func(trial) < func(target):
                    population[i] = np.copy(trial)
                evaluations += 1
                if evaluations >= self.budget:
                    break
            best_idx = np.argmin([func(ind) for ind in population])
            if best_solution is None or func(population[best_idx]) < func(best_solution):
                best_solution = np.copy(population[best_idx])
        return best_solution
# ```
