# Description: A novel metaheuristic algorithm using a combination of Particle Swarm Optimization (PSO) and Differential Evolution (DE) to efficiently optimize black box functions within a specified budget.
# Code:
# ```python
import numpy as np

class PSO_DE_Optimizer:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 20
        self.max_iter = budget // self.pop_size
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def create_population():
            return np.random.uniform(self.lb, self.ub, (self.pop_size, self.dim))

        def clip_individual(ind):
            return np.clip(ind, self.lb, self.ub)

        def fitness(ind):
            return func(ind)

        population = create_population()
        g_best = population[np.argmin([fitness(ind) for ind in population])]

        for _ in range(self.max_iter):
            for i in range(self.pop_size):
                p_best = population[np.argmin([fitness(ind) for ind in population])]
                r1, r2 = np.random.rand(self.dim), np.random.rand(self.dim)
                v = 0.5 * v + 0.8 * (p_best - population[i]) + 0.5 * (g_best - population[i])
                u = population[i] + 0.5 * (r1 * v + r2 * (population[np.random.choice(self.pop_size)] - population[np.random.choice(self.pop_size)]))
                u = clip_individual(u)

                if fitness(u) < fitness(population[i]):
                    population[i] = u

                if fitness(u) < fitness(g_best):
                    g_best = u

        return g_best
# ```
