# Description: Genetic Algorithm with Adaptive Mutation Rates: Utilizing a genetic algorithm with adaptive mutation rates to efficiently explore the search space and balance exploration and exploitation.
# Code:
# ```python
import numpy as np

class GeneticAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 50
        self.mutation_prob = 0.1
        self.selection_size = 10

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(-5.0, 5.0, size=(self.population_size, self.dim))

        def mutate(population):
            mutated_population = np.copy(population)
            for i in range(self.population_size):
                for j in range(self.dim):
                    if np.random.rand() < self.mutation_prob:
                        mutated_population[i, j] = np.random.uniform(-5.0, 5.0)
            return mutated_population

        def select_parents(population, fitness_values):
            selected_indices = np.argsort(fitness_values)[:self.selection_size]
            return population[selected_indices]

        def evaluate_population(population):
            return np.array([func(individual) for individual in population])

        population = initialize_population()
        fitness_values = evaluate_population(population)

        for _ in range(self.budget // self.population_size):
            parents = select_parents(population, fitness_values)
            offspring = mutate(parents)
            offspring_fitness = evaluate_population(offspring)

            combined_population = np.vstack((population, offspring))
            combined_fitness = np.concatenate((fitness_values, offspring_fitness))
            sorted_indices = np.argsort(combined_fitness)
            population = combined_population[sorted_indices[:self.population_size]]
            fitness_values = combined_fitness[sorted_indices[:self.population_size]]

        best_solution_index = np.argmin(fitness_values)
        best_solution = population[best_solution_index]

        return best_solution
