# Description: Quantum-Inspired Firefly Algorithm (QIFA) - Utilizes quantum mechanics principles to guide fireflies towards the global optimum by incorporating quantum entanglement and superposition.
# Code:
# ```python
import numpy as np

class QuantumFireflyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.alpha = 0.5
        self.beta_min = 0.2
        self.gamma = 1.0

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(-5.0, 5.0, size=(self.population_size, self.dim))

        def attract_fireflies(current, others):
            intensity = np.array([1 / (1 + func(individual)) for individual in others])
            distances = np.linalg.norm(current - others, axis=1)
            beta = self.beta_min + (1 - self.beta_min) * np.exp(-self.gamma * distances)
            new_position = current + self.alpha * np.sum((others - current) * beta[:, np.newaxis] * intensity[:, np.newaxis], axis=0)
            return np.clip(new_position, -5.0, 5.0)

        population = initialize_population()
        for _ in range(self.budget):
            for i, firefly in enumerate(population):
                other_fireflies = np.delete(population, i, axis=0)
                new_position = attract_fireflies(firefly, other_fireflies)
                if func(new_position) < func(firefly):
                    population[i] = new_position
        best_solution = population[np.argmin([func(individual) for individual in population])]
        return best_solution
# ```
