# Description: Novel Metaheuristic Algorithm based on Krill Herd Optimization for Black Box Optimization on BBOB Test Suite
# Code:
# ```python
import numpy as np

class KrillHerdOptimization:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.max_iter = budget // self.population_size

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(low=-5.0, high=5.0, size=(self.population_size, self.dim))

        def levy_flight_step():
            beta = 1.5
            sigma = (np.math.gamma(1 + beta) * np.math.sin(np.pi * beta / 2) / np.math.gamma((1 + beta) / 2) / beta) ** (1 / beta)
            u = np.random.normal(0, sigma, size=self.dim)
            v = np.random.normal(0, 1, size=self.dim)
            step = u / np.abs(v) ** (1 / beta)
            return step

        def update_position(position, step):
            new_position = position + step
            return np.clip(new_position, -5.0, 5.0)

        population = initialize_population()
        fitness_values = [func(individual) for individual in population]
        best_solution = population[np.argmin(fitness_values)]
        best_fitness = np.min(fitness_values)

        for _ in range(self.max_iter):
            for i in range(self.population_size):
                step = levy_flight_step()
                new_position = update_position(population[i], step)
                new_fitness = func(new_position)
                if new_fitness < fitness_values[i]:
                    population[i] = new_position
                    fitness_values[i] = new_fitness
                    if new_fitness < best_fitness:
                        best_solution = new_position
                        best_fitness = new_fitness

        return best_solution
# ```
