# Description: Novel Hybrid Metaheuristic Algorithm combining Particle Swarm Optimization (PSO) and Differential Evolution (DE) for black box optimization.
# Code: 
# ```python
import numpy as np
from scipy.optimize import differential_evolution

class HybridMetaheuristic:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim

    def __call__(self, func):
        def pso_de(func, bounds, budget):
            def objective(x):
                return func(x)

            bounds = [(l, u) for _ in range(self.dim) for l, u in zip([-5.0] * self.dim, [5.0] * self.dim)]
            result_pso = differential_evolution(objective, bounds, maxiter=budget)
            return result_pso.x, result_pso.fun

        best_solution, best_fitness = pso_de(func, self.dim, self.budget)
        return best_solution
