# Description: A novel metaheuristic optimization algorithm based on a combination of Differential Evolution (DE) and Simulated Annealing (SA) for solving black box optimization problems.
# Code:
# ```python
import numpy as np

class DESA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def random_solution():
            return np.random.uniform(self.lower_bound, self.upper_bound, self.dim)

        def clip_solution(solution):
            return np.clip(solution, self.lower_bound, self.upper_bound)

        def acceptance_probability(old_cost, new_cost, temperature):
            if new_cost < old_cost:
                return 1.0
            return np.exp((old_cost - new_cost) / temperature)

        best_solution = random_solution()
        best_cost = func(best_solution)
        current_solution = best_solution.copy()
        current_cost = best_cost
        temperature = 1.0

        for _ in range(self.budget):
            candidate_solution = clip_solution(current_solution + np.random.normal(size=self.dim))
            candidate_cost = func(candidate_solution)

            if acceptance_probability(current_cost, candidate_cost, temperature) > np.random.rand():
                current_solution = candidate_solution
                current_cost = candidate_cost

            if candidate_cost < best_cost:
                best_solution = candidate_solution
                best_cost = candidate_cost

            temperature *= 0.99  # Cooling schedule

        return best_solution
# ```
