# Description: Novel Firefly Algorithm for Black Box Optimization, inspired by the flashing behavior of fireflies to attract and move towards each other in a swarm intelligence approach.
# Code:
# ```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim, alpha=0.5, beta0=1.0, gamma=0.01):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta0 = beta0
        self.gamma = gamma

    def __call__(self, func):
        def attractiveness(r):
            return self.beta0 * np.exp(-self.gamma * r**2)

        def move(fireflies, best_firefly):
            for i in range(len(fireflies)):
                for j in range(len(fireflies)):
                    if func(fireflies[j]) < func(fireflies[i]):
                        r = np.linalg.norm(fireflies[j] - fireflies[i])
                        beta = attractiveness(r)
                        fireflies[i] += beta * (fireflies[j] - fireflies[i]) + self.alpha * (np.random.rand(self.dim) - 0.5)
                if func(fireflies[i]) < func(best_firefly):
                    best_firefly = np.copy(fireflies[i])
            return fireflies, best_firefly

        best_pos = np.random.uniform(-5.0, 5.0, self.dim)
        fireflies = [np.random.uniform(-5.0, 5.0, self.dim) for _ in range(self.budget)]
        for _ in range(self.budget):
            fireflies, best_pos = move(fireflies, best_pos)

        return best_pos
# ```
