# Description: Harmony Search algorithm with pitch adjustment and harmony memory consideration for global and local search.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim, hmcr=0.7, par=0.01, bw=0.01):
        self.budget = budget
        self.dim = dim
        self.hmcr = hmcr
        self.par = par
        self.bw = bw

    def __call__(self, func):
        def initialization():
            return np.random.uniform(-5.0, 5.0, self.dim)

        def harmony_search(func):
            harmony_memory = [initialization() for _ in range(self.dim)]
            best_solution = np.copy(harmony_memory[0])
            best_fitness = func(best_solution)

            for _ in range(self.budget):
                new_solution = np.copy(harmony_memory[np.random.randint(0, self.dim)])
                for i in range(self.dim):
                    if np.random.rand() < self.hmcr:
                        new_solution[i] = harmony_memory[np.random.randint(0, self.dim)][i]
                        if np.random.rand() < self.par:
                            new_solution[i] += np.random.uniform(-self.bw, self.bw)

                new_fitness = func(new_solution)
                if new_fitness < best_fitness:
                    best_solution = np.copy(new_solution)
                    best_fitness = new_fitness

                harmony_memory[np.argmax([func(h) for h in harmony_memory])] = np.copy(new_solution)

            return best_solution

        return harmony_search(func)
