# Description: Metaheuristic algorithm combining Particle Swarm Optimization (PSO) and Simulated Annealing (SA) to explore and exploit the search space efficiently.
# Code:
# ```python
import numpy as np

class PSOSA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim

    def __call__(self, func):
        def sa_move(x, best_x, t):
            return x + np.random.normal(0, 1) * (best_x - x) * np.exp(-t)

        def evaluate(x):
            return func(x)

        swarm_size = 20
        max_iterations = self.budget // swarm_size
        swarm = np.random.uniform(-5.0, 5.0, (swarm_size, self.dim))
        best_swarm_pos = np.copy(swarm)
        best_swarm_val = np.array([evaluate(x) for x in swarm])

        for t in range(max_iterations):
            for i in range(swarm_size):
                for d in range(self.dim):
                    swarm[i][d] = sa_move(swarm[i][d], best_swarm_pos[np.argmin(best_swarm_val)][d], t/max_iterations)
                val = evaluate(swarm[i])
                if val < best_swarm_val[i]:
                    best_swarm_val[i] = val
                    best_swarm_pos[i] = np.copy(swarm[i])

        return best_swarm_pos[np.argmin(best_swarm_val)]
# ```
