# Description: Harmony Search Optimization Algorithm: A metaheuristic algorithm inspired by the musical improvisation process that harmonizes better solutions by combining past good solutions.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def initialize_harmony_memory():
            return np.random.uniform(self.lower_bound, self.upper_bound, (self.budget, self.dim))

        def fitness(solution):
            return func(solution)

        def improvise(harmony_memory):
            new_harmony = np.zeros((self.dim,))
            for i in range(self.dim):
                if np.random.rand() < 0.5:
                    new_harmony[i] = np.random.uniform(self.lower_bound, self.upper_bound)
                else:
                    idx = np.random.randint(self.budget)
                    new_harmony[i] = harmony_memory[idx, i]
            return new_harmony

        harmony_memory = initialize_harmony_memory()
        for _ in range(self.budget):
            new_solution = improvise(harmony_memory)
            if fitness(new_solution) < fitness(harmony_memory[-1]):
                harmony_memory[-1] = new_solution
                harmony_memory = harmony_memory[np.argsort([fitness(sol) for sol in harmony_memory])]
        return harmony_memory[0]
# ```
