# Description: Utilizing Differential Evolution to optimize black box functions within a specified budget.
# Code: 
# ```python
import numpy as np

class DifferentialEvolution:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 10
        self.CR = 0.9
        self.F = 0.8

    def __call__(self, func):
        def target_function(x):
            return func(x)

        bounds = [(-5.0, 5.0) for _ in range(self.dim)]
        population = np.random.uniform(-5.0, 5.0, (self.population_size, self.dim))

        for _ in range(self.budget):
            for i in range(self.population_size):
                target_vector = population[np.random.choice([idx for idx in range(self.population_size) if idx != i], 2, replace=False)]
                mutant_vector = np.clip(population[target_vector[0]] + self.F * (population[target_vector[1]] - population[target_vector[2]]), -5.0, 5.0)
                trial_vector = np.array([mutant_vector[j] if np.random.rand() < self.CR or j == np.random.choice(range(self.dim)) else population[i, j] for j in range(self.dim)])

                if target_function(trial_vector) < target_function(population[i]):
                    population[i] = trial_vector

        best_solution = population[np.argmin([target_function(individual) for individual in population])]
        return best_solution
