# Description: Firefly Algorithm is inspired by the flashing behavior of fireflies. It uses the attractiveness between fireflies to guide the search towards the optimal solution.
# Code:
```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget=10000, dim=10, alpha=0.5, beta0=1.0, gamma=0.01):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta0 = beta0
        self.gamma = gamma
        self.f_opt = np.Inf
        self.x_opt = None

    def attractiveness(self, x_i, x_j):
        return np.exp(-self.gamma * np.linalg.norm(x_i - x_j)**2)

    def move_firefly(self, x_i, x_j):
        r = np.linalg.norm(x_i - x_j)
        beta = self.beta0 * np.exp(-self.alpha * r**2)
        return x_i + beta * (x_j - x_i) + 0.01 * np.random.normal(size=self.dim)

    def __call__(self, func):
        fireflies = np.random.uniform(func.bounds.lb, func.bounds.ub, size=(self.budget, self.dim))

        for i in range(self.budget):
            for j in range(self.budget):
                if func(fireflies[j]) < func(fireflies[i]):
                    fireflies[i] = self.move_firefly(fireflies[i], fireflies[j])

            f = func(fireflies[i])
            if f < self.f_opt:
                self.f_opt = f
                self.x_opt = fireflies[i]

        return self.f_opt, self.x_opt
```

# Description: Improved Firefly Algorithm with dynamic attraction parameter adaptation
# Code:
```python
class ImprovedFireflyAlgorithm(FireflyAlgorithm):
    def __init__(self, budget=10000, dim=10, alpha=0.5, beta0=1.0, gamma=0.01):
        super().__init__(budget, dim, alpha, beta0, gamma)
        self.alpha_min = 0.2

    def __call__(self, func):
        fireflies = np.random.uniform(func.bounds.lb, func.bounds.ub, size=(self.budget, self.dim))

        for i in range(self.budget):
            for j in range(self.budget):
                if func(fireflies[j]) < func(fireflies[i]):
                    r = np.linalg.norm(fireflies[i] - fireflies[j])
                    beta = self.beta0 * np.exp(-self.alpha * r**2)
                    fireflies[i] = fireflies[i] + beta * (fireflies[j] - fireflies[i]) + 0.01 * np.random.normal(size=self.dim)
                    self.alpha = max(self.alpha_min, self.alpha * 0.99)  # Dynamic adaptation

            f = func(fireflies[i])
            if f < self.f_opt:
                self.f_opt = f
                self.x_opt = fireflies[i]

        return self.f_opt, self.x_opt
