# Description: Firefly Algorithm is inspired by the flashing behavior of fireflies to find the optimal solution by attracting each other towards brightness.
# Code:
```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget=10000, dim=10):
        self.budget = budget
        self.dim = dim
        self.alpha = 0.2  # alpha parameter (randomness)
        self.beta0 = 1.0  # beta parameter (attractiveness)
        self.f_opt = np.Inf
        self.x_opt = None

    def attractiveness(self, x, y):
        return np.exp(-self.beta0 * np.linalg.norm(x - y))

    def move_firefly(self, x, y):
        r = np.linalg.norm(x - y)
        beta = self.beta0 * np.exp(-self.alpha*r**2)
        return x + beta*(y - x) + self.alpha*(np.random.rand(self.dim) - 0.5)

    def __call__(self, func):
        fireflies = np.random.uniform(func.bounds.lb, func.bounds.ub, (self.budget, self.dim))

        for i in range(self.budget):
            for j in range(self.budget):
                if func(fireflies[j]) < func(fireflies[i]):
                    fireflies[i] = self.move_firefly(fireflies[i], fireflies[j])

            f = func(fireflies[i])
            if f < self.f_opt:
                self.f_opt = f
                self.x_opt = fireflies[i]

        return self.f_opt, self.x_opt
```

